// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@aave/core-v3/contracts/flashloan/base/FlashLoanSimpleReceiverBase.sol";
import "@aave/core-v3/contracts/interfaces/IPoolAddressesProvider.sol";
import "@aave/core-v3/contracts/interfaces/IPool.sol";

contract FlashLoanReceiver is FlashLoanSimpleReceiverBase {
    using SafeERC20 for IERC20;

    address public owner;

    constructor(address _addressProvider) FlashLoanSimpleReceiverBase(IPoolAddressesProvider(_addressProvider)) {
        owner = msg.sender;
    }

    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external override returns (bool) {
        // Decode params
        (address router, address tokenIn, address tokenOut, uint256 deadline) =
            abi.decode(params, (address, address, address, uint256));

        // This is where you would implement your trading logic

        // At the end of your logic, you need to repay the flash loan
        uint256 amountToRepay = amount + premium;

        // Approve the Pool to pull the amount + premium
        IERC20(asset).approve(address(POOL), amountToRepay);

        return true;
    }

    function requestFlashLoan(
        address token,
        uint256 amount,
        bytes calldata params
    ) external {
        require(msg.sender == owner, "Only owner");
        IPool(address(POOL)).flashLoanSimple(
            address(this),
            token,
            amount,
            params,
            0
        );
    }

    function withdraw(address token) external {
        require(msg.sender == owner, "Only owner");
        IERC20 tokenContract = IERC20(token);
        uint256 balance = tokenContract.balanceOf(address(this));
        tokenContract.safeTransfer(owner, balance);
    }

    // Function to approve tokens (useful for initial setup)
    function approveToken(address token, address spender, uint256 amount) external {
        require(msg.sender == owner, "Only owner");
        IERC20(token).approve(spender, amount);
    }

    // Function to revoke token approvals
    function revokeTokenApproval(address token, address spender) external {
        require(msg.sender == owner, "Only owner");
        IERC20(token).approve(spender, 0);
    }

    // Emergency function to recover any stuck tokens
    function emergencyTokenRecovery(address token) external {
        require(msg.sender == owner, "Only owner");
        IERC20 tokenContract = IERC20(token);
        uint256 balance = tokenContract.balanceOf(address(this));
        tokenContract.safeTransfer(owner, balance);
    }

    // Emergency function to recover ETH
    function emergencyEthRecovery() external {
        require(msg.sender == owner, "Only owner");
        payable(owner).transfer(address(this).balance);
    }

    // Allow the contract to receive ETH
    receive() external payable {}
}

